import { createServer } from "http";
import Stripe from "stripe";
if (!process.env.STRIPE_SECRET_KEY) {
  throw new Error("Missing required Stripe secret: STRIPE_SECRET_KEY");
}
var stripe = new Stripe(process.env.STRIPE_SECRET_KEY);
var SUBSCRIPTION_PRICING = {
  selection: { 3: 55, 6: 110 },
  cuvee: { 3: 110, 6: 215 },
  prestige: { 3: 250, 6: 500 },
  champagne: { 3: 140, 6: 275 }
};
var EXTRAS_PRICING = {
  selection: { champagne: 20, dessert: 20 },
  cuvee: { champagne: 35, dessert: 35 },
  prestige: { champagne: 85, dessert: 85 },
  champagne: { champagne: 0, dessert: 0 }
};
async function registerRoutes(app2) {
  app2.post("/api/create-payment-intent", async (req, res) => {
    try {
      const { tier, bottles, extras } = req.body;
      if (!tier || !bottles) {
        return res.status(400).json({
          message: "Missing required fields: tier, bottles"
        });
      }
      if (!SUBSCRIPTION_PRICING[tier]) {
        return res.status(400).json({
          message: "Invalid subscription tier"
        });
      }
      if (bottles !== 3 && bottles !== 6) {
        return res.status(400).json({
          message: "Invalid bottle count. Must be 3 or 6"
        });
      }
      let amount = SUBSCRIPTION_PRICING[tier][bottles];
      if (extras && Array.isArray(extras)) {
        const tierExtras = EXTRAS_PRICING[tier];
        for (const extra of extras) {
          if (extra === "champagne" || extra === "dessert") {
            amount += tierExtras[extra];
          }
        }
      }
      const paymentIntent = await stripe.paymentIntents.create({
        amount: Math.round(amount * 100),
        currency: "eur",
        metadata: {
          tier,
          bottles: bottles.toString(),
          extras: extras ? JSON.stringify(extras) : "[]"
        }
      });
      res.json({ clientSecret: paymentIntent.client_secret });
    } catch (error) {
      console.error("Error creating payment intent:", error);
      res.status(500).json({ message: "Error creating payment intent: " + error.message });
    }
  });
  const httpServer = createServer(app2);
  return httpServer;
}

